import React, { Component, useState, useEffect } from "react";
import { RouteComponentProps } from "react-router-dom";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";
//  npm install @emotion/react
//  npm install @emotion/styled

interface HomePagePropsInterface extends RouteComponentProps<{}> {
  // Other props that belong to component it self not Router
}

const HomePage: React.FC<HomePagePropsInterface> = () => {
  const [extension, setExtension] = useState("");

  function buildLink(company: string): string {
    const link = "/visual/" + company + "/close";
    return link;
  }

  const ffiveExtension = "/visual/ffive/close";
  const citrixExtension = "/visual/citrix/close";
  const amzExtension = "/visual/amazon/close";
  const appleExtension = "/visual/apple/close";

  function handleSelection(ChangeEvent) {
    const value: string = ChangeEvent.target.value;
    setExtension(buildLink(value));
  }

  const LinkName = styled.div`
    paddingbottom: 1rem;
  `;

  return (
    <div style={{ alignItems: "center" }}>
      <h1 style={{ alignItems: "center" }}>
        Welcome to The Graphind Tool Demo
      </h1>
      <div>
        <div>
          <h2>Please select from the following option</h2>
        </div>
        <form>
          <div className="radio">
            <label>
              <input type="radio" value="ffive" checked={true} />
              Option 1
            </label>
          </div>
          <div className="radio">
            <label>
              <input type="radio" value="citrix" />
              Option 2
            </label>
          </div>
          <div className="radio">
            <label>
              <input type="radio" value="amazon" />
              Option 3
            </label>
          </div>
          <div className="radio">
            <label>
              <input type="radio" value="apple" />
              Option 3
            </label>
          </div>
        </form>
        <div>
          <Link to={extension} type="submit" className="btn btn-default">
            Visualize The selected Stock
          </Link>
        </div>

        <LinkName>
          <Link to={ffiveExtension} type="button">
            Visualize F-5 Stocks
          </Link>
        </LinkName>
        <LinkName>
          <Link to={citrixExtension} type="button">
            Visualize Citrix Stocks
          </Link>
        </LinkName>
        <LinkName>
          <Link to={amzExtension} type="button">
            Visualize Amazon Stocks
          </Link>
        </LinkName>
        <LinkName>
          <Link to={appleExtension} type="button">
            Visualize Apple Stocks
          </Link>
        </LinkName>
      </div>
    </div>
  );
};

export default HomePage;
