import React, { Component, useState, useEffect } from "react";
import { RouteComponentProps } from "react-router-dom";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";
//  npm install @emotion/react
//  npm install @emotion/styled

interface HomePagePropsInterface extends RouteComponentProps<{}> {
  // Other props that belong to component it self not Router
}

const HomePage: React.FC<HomePagePropsInterface> = () => {
  const [extension, setExtension] = useState("");
  const [selectedOption, setSelectedOption] = useState("");
  const [multipleExtension, setMultipleExtension] = useState("");
  const [isSelected, SetIsSelected] = useState<boolean>();

  const selectedGraph: string[] = [];

  let selectedCheckboxes = new Set();

  function buildLink(company: string): string {
    const link = "/visual/" + company + "/close";
    return link;
  }

  function buildLinkMultiple(company: string): string {
    const link = "/visualMultiple/" + company + "/close";
    return link;
  }

  function toogleSelection(value: string): boolean {
    if (selectedCheckboxes.has(value)) {
      selectedCheckboxes.delete(value);
    } else {
      selectedCheckboxes.add(value);
    }
    if (selectedGraph.find((item) => item === value)) {
      return false;
    } else {
      selectedGraph.push(value);
      return true;
    }
  }

  //visualMultiple
  function handleCheckedBoxes(ChangeEvent) {
    const value: string = ChangeEvent.target.value;
    if (toogleSelection(value)) {
      buildMultipeLink(value);
      console.log(multipleExtension);
      setMultipleExtension(buildLinkMultiple(value));
    }
  }

  function buildMultipeLink(value: string) {
    let link: string = multipleExtension.concat("&&");
    if (selectedGraph.find((elmt) => elmt === value) === undefined) {
      selectedGraph.push(value);
    }
    for (let i = 0; i < setSelectedOption.length; i++) {
      setMultipleExtension(link.concat(value));
    }
  }

  function handleSelection(ChangeEvent) {
    const value: string = ChangeEvent.target.value;
    setExtension(buildLink(value));
    setSelectedOption(value);
  }

  const LinkName = styled.div`
    paddingbottom: 1rem;
  `;

  function createRadioButton(tag: string, name: string) {
    return (
      <div className="radio">
        <label>
          <input
            type="radio"
            value={tag}
            checked={selectedOption === tag}
            onChange={handleSelection}
          />
          {name}
        </label>
      </div>
    );
  }

  return (
    <div style={{ alignItems: "center", margin: "25px" }}>
      <h1 style={{ alignItems: "center" }}>
        Welcome to The Graphing Tool Demo
      </h1>
      <div>
        <div>
          <h2>Please select from the following option</h2>
        </div>
        <form>
          {createRadioButton("ffive", "F-5")}
          <div className="radio">
            <label>
              <input
                type="radio"
                value="ffive"
                checked={selectedOption === "ffive"}
                onChange={handleSelection}
              />
              F-5
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="citrix"
                checked={selectedOption === "citrix"}
                onChange={handleSelection}
              />
              Citrix
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="amazon"
                checked={selectedOption === "amazon"}
                onChange={handleSelection}
              />
              Amazon
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="apple"
                checked={selectedOption === "apple"}
                onChange={handleSelection}
              />
              Apple
            </label>
          </div>
        </form>
        <div style={{ paddingTop: "15px" }}>
          <Link to={extension} type="submit" className="btn btn-default">
            Visualize The selected Stock
          </Link>
        </div>
      </div>

      <div>
        <div>
          <h2>Please select 1 or more from the following option to display</h2>
        </div>
        <form>
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="ffive"
                checked={
                  selectedGraph.find((item) => item === "ffive") === undefined
                }
                onChange={handleCheckedBoxes}
              />
              F-5
            </label>
          </div>
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="citrix"
                checked={
                  selectedGraph.find((item) => item === "citrix") === undefined
                }
                onChange={handleCheckedBoxes}
              />
              Citrix
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="checkbox"
                value="amazon"
                checked={
                  selectedGraph.find((item) => item === "amazon") === undefined
                }
                onChange={handleCheckedBoxes}
              />
              Amazon
            </label>
          </div>
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="apple"
                checked={
                  selectedGraph.find((item) => item === "apple") === undefined
                }
                onChange={handleCheckedBoxes}
              />
              Apple
            </label>
          </div>
        </form>
        <div style={{ paddingTop: "15px" }}>
          <Link
            to={multipleExtension}
            type="submit"
            className="btn btn-default"
          >
            Visualize The selected Stocks
          </Link>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
