import React, { Component, useState, useEffect } from "react";
import { RouteComponentProps } from "react-router-dom";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";
import * as companies from "../data/urlMatcher.json";
// npm install @emotion/react
// npm install @emotion/styled

interface HomePagePropsInterface extends RouteComponentProps<{}> {
  // Other props that belong to component it self not Router
}

const HomePage: React.FC<HomePagePropsInterface> = () => {
  const [extension, setExtension] = useState("");
  const [selectedOption, setSelectedOption] = useState("");
  const [multipleExtension, setMultipleExtension] = useState<string>("ffive&&");
  const [isSelected, SetIsSelected] = useState<boolean>();
  const [CompanyNames, SetCompanyNames] = useState<string[]>([]);
  const [CompanyIds, SetCompanyIds] = useState<string[]>();

  const companyList = companies.companies;

  useEffect(() => {
    let namesArray: string[] = [];
    let idsArray: string[] = [];
    for (let i: number = 0; i < companyList.length; i++) {
      namesArray.push(companyList[i].name);
      idsArray.push(companyList[i].id);
    }
    SetCompanyNames(namesArray);
    SetCompanyIds(idsArray);
  }, []);

  const selectedGraph: string[] = [];

  let selectedCheckboxes = new Set<string>();

  function buildLink(company: string): string {
    const link = "/visual/" + company + "/close";
    return link;
  }

  function buildLinkMultiple(company: string): string {
    const link = "/visualMultiple/" + company + "/close";
    return link;
  }

  function toogleSelection(value: string): boolean {
    return multipleExtension.includes(value);
  }

  //visualMultiple
  function handleCheckedBoxes(ChangeEvent) {
    const value: string = ChangeEvent.target.value;
    console.log(value);
    if (multipleExtension.includes(value)) {
      const tempArray: string[] = multipleExtension.split("&&");
      console.log(tempArray);
      let tempString = "";
      for (const elmt of tempArray) {
        if (elmt.trim() !== value) {
          tempString = tempString.concat(elmt);
        }
      }
      setMultipleExtension(tempString);
    } else {
      setMultipleExtension(multipleExtension.concat(value.concat("&&")));
    }
    console.log(multipleExtension);
    //setMultipleExtension(buildLinkMultiple(value));
  }

  function handleSelection(ChangeEvent) {
    const value: string = ChangeEvent.target.value;
    setExtension(buildLink(value));
    setSelectedOption(value);
  }

  const LinkName = styled.div`
    paddingbottom: 1rem;
  `;

  function createRadioButton(tag: string, name: string) {
    return (
      <div className="radio">
        <label>
          <input
            type="radio"
            value={tag}
            checked={selectedOption === tag}
            onChange={handleSelection}
          />
          {name}
        </label>
      </div>
    );
  }

  function createCheckBoxes(tag: string, name: string) {
    return (
      <div className="checkbox">
        <label>
          <input
            type="checkbox"
            value={tag}
            checked={toogleSelection(tag)}
            onChange={handleCheckedBoxes}
          />
          {name}
        </label>
      </div>
    );
  }

  return (
    <div style={{ alignItems: "center", margin: "25px" }}>
      <h1 style={{ alignItems: "center" }}>
        Welcome to The Graphing Tool Demo
      </h1>
      <div>
        <div>
          <h2>Please select from the following option</h2>
        </div>
        <form>
          {createRadioButton("ffive", "F-5")}
          {createRadioButton("citrix", "Citrix")}
          {createRadioButton("amazon", "Amazon")}
          {createRadioButton("apple", "Apple")}
        </form>
        <div style={{ paddingTop: "15px" }}>
          <Link to={extension} type="submit" className="btn btn-default">
            Visualize The selected Stock
          </Link>
        </div>
      </div>

      <div>
        <div>
          <h2>Please select 1 or more from the following option to display</h2>
        </div>
        <form>
          {createCheckBoxes("ffive", "F-5")}
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="ffive"
                checked={toogleSelection("ffive")}
                onChange={handleCheckedBoxes}
              />
              F-5
            </label>
          </div>
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="citrix"
                checked={toogleSelection("citrix")}
                onChange={handleCheckedBoxes}
              />
              Citrix
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="checkbox"
                value="amazon"
                checked={toogleSelection("amazon")}
                onChange={handleCheckedBoxes}
              />
              Amazon
            </label>
          </div>
          <div className="checkbox">
            <label>
              <input
                type="checkbox"
                value="apple"
                checked={toogleSelection("apple")}
                onChange={handleCheckedBoxes}
              />
              Apple
            </label>
          </div>
        </form>
        <div style={{ paddingTop: "15px" }}>
          <Link
            to={multipleExtension}
            type="submit"
            className="btn btn-default"
          >
            Visualize The selected Stocks
          </Link>
          <button onClick={handleCheckedBoxes}>Activate Lasers</button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
