import React, { Component, useState, useEffect } from "react";
import { RouteComponentProps } from "react-router-dom";
// import Plot from "react-plotly.js";
import * as companies from "../data/urlMatcher.json";
import { Link } from "react-router-dom";

import Plotly from "plotly.js";
import createPlotlyComponent from "react-plotly.js/factory";
const Plot = createPlotlyComponent(Plotly);

interface CombineGraphPropsInterface
  extends RouteComponentProps<{ company: string; option: string }> {
  // Other props that belong to component it self not Router
}

const CombineGraph: React.FC<CombineGraphPropsInterface> = (
  props: CombineGraphPropsInterface
) => {
  // setting to props parameters
  const company = props.match.params.company;
  const option = props.match.params.option;

  const companyList: string[] = company.split("+").filter((elm) => elm !== "");
  console.log(companyList);

  type DailyExchange = {
    date: string;
    open: number;
    high: number;
    low: number;
    close: number;
    adjClose?: number;
    volume: number;
  };

  type Marker = {
    color: string;
  };

  type PlotGraph = {
    x: any[];
    y: any[];
    type: string;
    mode?: string;
    name?: string;
    yaxis: string;
  };

  let dailyValues: DailyExchange[] = [];
  let dailyValuesOrdinate: number[] = [];
  let dates: Date[] = [];

  // const TEST_COMP = [
  //   "https://raw.githubusercontent.com/CS-21-07/jsonStorage/main/ffivestock.json",
  //   "https://raw.githubusercontent.com/CS-21-07/jsonStorage/main/citrixstock.json",
  // ];
  // const TEST_COMP_NAME = ["F-5", "Citrix"];

  let plotGraphData: any[] = [];

  const [fetchUrl, setfetchUrl] = useState<any>("");
  const [trigger, setTrigger] = useState<boolean>(true);
  const [extension, setExtension] = useState("");
  const [value, setValue] = useState<string>("Close");
  const [GraphTitle, setGraphTitle] = useState("");
  const [dataGraph, setDataGraph] = useState<any[]>([]);
  const [xx, setxx] = useState<Date[]>([]);
  const [yy, setyy] = useState<number[]>([]);
  const [whichAxes, setWhichY] = useState<number>(0);

  useEffect(() => {
    async function myFetch(toFetch: string) {
      console.log("fetching2: " + toFetch);
      const response = await fetch(toFetch);
      // return Promise.resolve(response);
      return response;
    }

    let myFetches: any[] = [];

    companyList.forEach((id) => {
      if (id !== "") {
        const fetching = companies.companies.find((comp) => comp.id === id)
          ?.JsonUrl;
        console.log("we are fetching: " + fetching);

        const companyName = companies.companies.find((comp) => comp.id === id)
          ?.name;
        console.log("Company we are fetching: " + companyName);
        setfetchUrl(fetching);
        console.log("fetching: " + fetching);
        myFetches.push(myFetch(fetchUrl));
      }
    });

    Promise.all(myFetches)
      .then((files) => {
        files.forEach((file) => {
          process(file.json());
        });
      })
      .catch((err) => {
        console.log("There was an error in fetching", err);
      });

    function process(prom) {
      dailyValues = [];
      prom
        .then((data) => {
          const stockEvents: any[] = data;
          console.log(stockEvents?.length);
          console.log(stockEvents);
          for (let i: number = 0; i < stockEvents?.length; i++) {
            let tempString: string = JSON.stringify(stockEvents[i]); //extract(stockEvents[i]);
            let obj = JSON.parse(tempString);
            let dailyExchangeObj: DailyExchange = {
              date: obj.Date,
              high: obj.High,
              low: obj.Low,
              open: obj.Open,
              volume: obj.Volume,
              close: obj.Close,
            };
            dailyValues.push(dailyExchangeObj);
          }
          populateGraphValue(dailyValues);
        })
        .catch((err) => {
          console.log("There was an error in fetching", err);
        });
    }
    setValue(option);
  }, []);

  function populateGraphValue(dailyValuesList: any[]): void {
    console.log(dailyValuesList);
    for (let i = 0; i < dailyValuesList.length; i++) {
      manageOption(dailyValuesList[i]);
      dates.push(toDateObject(dailyValuesList[i].date));
    }
    setxx(dates);
    setyy(dailyValuesOrdinate);
    console.log(xx);
    console.log(yy);

    dates = [];
    dailyValuesOrdinate = [];

    let yaxe = "y";
    if (whichAxes === 0) {
      yaxe.concat("");
      setWhichY(whichAxes + 1);
    } else {
      yaxe.concat(whichAxes.toString());
      setWhichY(whichAxes + 1);
    }

    let graphData: PlotGraph = {
      x: xx,
      y: yy,
      type: "scatter",
      mode: "lines",
      yaxis: yaxe,
      name: "GraphTitle".concat(whichAxes.toString()),
    };
    console.log(graphData);
    plotGraphData = dataGraph;
    plotGraphData.push(graphData);
    setDataGraph(plotGraphData);
    console.log("Number of graph in my list " + plotGraphData.length);
  }

  function manageOption(obj: DailyExchange): void {
    switch (option) {
      case "close":
        dailyValuesOrdinate.push(obj.close);
        break;
      case "high":
        dailyValuesOrdinate.push(obj.high);
        break;
      case "low":
        dailyValuesOrdinate.push(obj.low);
        break;
      case "volume":
        dailyValuesOrdinate.push(obj.volume);
        break;
      case "open":
        dailyValuesOrdinate.push(obj.open);
        break;
      default:
        dailyValuesOrdinate.push(obj.close);
        break;
    }
  }

  function toDateObject(dateString: string): Date {
    const temp: string[] = dateString.split("-");
    const date: Date = new Date();
    date.setFullYear(parseInt(temp[0]), parseInt(temp[1]), parseInt(temp[2]));
    return date;
  }

  function handleChange(event) {
    const linkvalue: string = event.target.value;
    setValue(linkvalue);
    setExtension("/visualMultiple/" + company + "/" + linkvalue.toLowerCase());
    console.log(extension);
  }

  return (
    <div style={{ alignItems: "center", margin: "12" }}>
      <div>
        <Link to={"/home"} type="button">
          Go to Home
        </Link>
      </div>
      <div>
        <label>
          Pick the chart to display:
          <select value={value} onChange={handleChange}>
            <option value="Close">Select from Menu</option>
            <option value="Open">Open</option>
            <option value="Volume">Volume</option>
            <option value="Close">Close</option>
            <option value="High">High</option>
            <option value="Low">Low</option>
          </select>
        </label>
        <a href={extension}>Visualize </a>
      </div>
      <div>
        <Plot
          data={dataGraph}
          layout={{
            width: 1000,
            height: 600,
            title: GraphTitle,
          }}
        />
      </div>
    </div>
  );
};

export default CombineGraph;
